function encriptar(dados){let mensx="",l,i,j=0,ch=chaveJs;const lchaveJs=chaveJs.length,ldado=dados.length;for(i=0;i<ldado;i++)j++,l=Asc(dados.substr(i,1))+Asc(ch.substr(j,1)),j==lchaveJs&&(j=1),l>255&&(l-=256),mensx+=Chr(l);return mensx}function descriptar(dados){let mensx="",l,i,j=0,ch=chaveJs;const lchaveJs=chaveJs.length,ldado=dados.length;for(i=0;i<dados.length;i++)j++,l=Asc(dados.substr(i,1))-Asc(ch.substr(j,1)),j==lchaveJs&&(j=1),l<0&&(l+=256),mensx+=Chr(l);return mensx}function Asc(String){return String.charCodeAt(0)}function Chr(AsciiNum){return String.fromCharCode(AsciiNum)}function mostrarDadoNormal(dado){return console.log("Dado normal: "+dado),dado}function mostrarDadoEncriptado(dado){const dadoEncriptado=encriptar(dado);return console.log("Dado original encriptado: "+dadoEncriptado),dadoEncriptado}function mostrarDadoDescriptado(dado){const dadoOriginal=descriptar(dado);return console.log("Dado original desencriptado: "+dadoOriginal),dadoOriginal}const btnAcessar=document.querySelector("#acessar");btnAcessar.addEventListener("click",()=>{const senha=document.getElementById("senha").value;let dado=mostrarDadoNormal(senha);dado=mostrarDadoEncriptado(dado),mostrarDadoDescriptado(dado)});